.slider-nav {
  bottom: rem(20px);
  counter-reset: navSlide;
  display: flex;
  font-size: rem(10px);
  justify-content: center;
  left: 50%;
  margin-bottom: rem(-40px);
  opacity: 0;
  position: absolute;
  transform: translateX(-50%);
  transition: opacity .3s, margin-bottom .4s;
  z-index: 1;
  &-item {
    align-items: center;
    background-color: rgba(255, 255, 255, 0.75);
    border-radius: 50%;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.4);
    color: transparent;
    cursor: pointer;
    display: flex;
    font-weight: 700;
    height: rem(15px);
    justify-content: center;
    position: relative;
    width: rem(15px);
    &:not(:first-of-type) {
      margin-left: rem(10px);
    }
    &::after {
      counter-increment: navSlide;
      content: counter(navSlide);
    }
  }
}

sources.src\\scss\\components\\slider\\_slide.sc:
.slider-slide {
  @include handleDefaultSlideAnimation($max-slides);
  border-radius: rem(12px);
  counter-increment: currentSlide;
  display: flex;
  font-size: rem(15px);
  height: 100%;
  line-height: normal;
  min-width: 100%;
  overflow: hidden;
  position: relative;
  transition: margin-left;
  vertical-align: top;
  white-space: normal;
}

.slider-content {
  background-color: #fff;
  flex-shrink: 0;
  height: 100%;
  margin-left: rem(-$slide-content-width);
  padding: rem(20px) rem(30px);
  position: relative;
  top: 0;
  width: rem($slide-content-width);
  z-index: 1;
}

.slider-title {
  &::after {
    content: ' #' counter(currentSlide);
  }
}

.slider-image {
  height: 100%;
  object-fit: cover;
}

sources.src\\scss\\components\\slider\\_slides.s:
.slider-slides {
  list-style-type: none;
  position: relative;
  height: 400px;
  z-index: 1;
  font-size: 0;
  line-height: 0;
  margin: 0 auto;
  padding: 0;
  overflow: hidden;
  white-space: nowrap;
  box-sizing: border-box;
  display: flex;
}

.slider-wrapper {
  background-color: #000;
  border-radius: rem(12px);
  box-shadow: 0 0 25px rgba(0, 0, 0, .2);
  counter-reset: currentSlide;
  margin-left: auto;
  margin-right: auto;
  max-width: $slider-width;
  perspective: 1000px;
  &:hover {
    .slider-nav {
      margin-bottom: 0;
      opacity: 1;
    }
  }
}

